mapper.select:
		select
			favoritenum,myurl,userid
		from
			favorite
		where
		
			favoritenum = #{favoritenum}
		
		
		and
			myurl = #{myurl}
		
  		
  		and
  			userid = #{userid}

mapper.update:
update
	favorite
set
	myurl = #{myurl}, userid = #{userid}
where
	favoritenum = #{favoritenum}
